import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { siteContent } from '../data/data';
import { useAuth } from '../contexts/AuthContext';

const Login = () => {
  const { loginPage } = siteContent;
  const { login, error } = useAuth();
  const navigate = useNavigate();

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const success = await login(email, password);
      if (success) {
        navigate('/profile');
      }
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50">
      <div className="absolute top-4 left-4">
        <Link to="/" className="text-lg font-bold">
          {siteContent.navigation.logo}
        </Link>
      </div>

      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-sm">
        <h1 className="text-2xl font-bold text-center mb-6">{loginPage.title}</h1>

        {error && (
          <div className="bg-red-100 text-red-700 p-3 rounded-md mb-4">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label htmlFor="email" className="block text-gray-700 mb-2">
              {loginPage.emailLabel}
            </label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder={loginPage.emailPlaceholder}
              className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-gray-200"
              required
            />
          </div>

          <div className="mb-6">
            <label htmlFor="password" className="block text-gray-700 mb-2">
              {loginPage.passwordLabel}
            </label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder={loginPage.passwordPlaceholder}
              className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-gray-200"
              required
            />
          </div>

          <button
            type="submit"
            className="w-full bg-black text-white py-2 rounded-md hover:bg-gray-800 transition duration-200"
            disabled={isSubmitting}
          >
            {isSubmitting ? 'Connexion...' : loginPage.signInButton}
          </button>
        </form>

        <div className="mt-4 flex justify-between">
          <a href="#" className="text-red-500 text-sm hover:underline">
            {loginPage.forgotPassword}
          </a>
          <Link to="/register" className="text-blue-500 text-sm hover:underline">
            {loginPage.signUp}
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
