import React, { createContext, useState, useContext } from 'react';
import { authService } from '../data/data';

export const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const login = async (email, password) => {
    setLoading(true);
    setError(null);
    try {
      const result = authService.login(email, password);
      if (result.success) {
        setUser(result.user);
        return true;
      } else {
        setError(result.message);
        return false;
      }
    } catch (err) {
      setError('Une erreur est survenue lors de la connexion');
      return false;
    } finally {
      setLoading(false);
    }
  };

  const register = async (userInfo) => {
    setLoading(true);
    setError(null);
    try {
      const result = authService.register(userInfo);
      if (result.success) {
        setUser(result.user);
        return true;
      } else {
        setError(result.message);
        return false;
      }
    } catch (err) {
      setError('Une erreur est survenue lors de l\'inscription');
      return false;
    } finally {
      setLoading(false);
    }
  };

  const updateProfile = async (userInfo) => {
    setLoading(true);
    setError(null);
    try {
      if (!user) {
        setError('Vous devez être connecté pour mettre à jour votre profil');
        return false;
      }
      const result = authService.updateProfile(user.id, userInfo);
      if (result.success) {
        setUser(result.user);
        return true;
      } else {
        setError(result.message);
        return false;
      }
    } catch (err) {
      setError('Une erreur est survenue lors de la mise à jour de votre profil');
      return false;
    } finally {
      setLoading(false);
    }
  };

  const logout = () => {
    setUser(null);
  };

  const value = {
    user,
    loading,
    error,
    isAuthenticated: !!user,
    login,
    register,
    updateProfile,
    logout,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};
