import axios from 'axios';

// Cr√©er une instance d'Axios avec une configuration par d√©faut
const api = axios.create({
  baseURL: '/api', // Utiliser une URL relative qui sera dirig√©e par notre proxy Nginx
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json',
  }
});

// Intercepteur de requ√™te pour ajouter le token d'authentification
api.interceptors.request.use(
  (config) => {
    console.log(`üöÄ Requ√™te envoy√©e vers: ${config.method.toUpperCase()} ${config.url}`, {
      headers: config.headers,
      data: config.data
    });

    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    console.error('‚ùå Erreur lors de la requ√™te:', error);
    return Promise.reject(error);
  }
);

// Intercepteur de r√©ponse pour g√©rer les erreurs
api.interceptors.response.use(
  (response) => {
    console.log(`‚úÖ R√©ponse re√ßue de: ${response.config.method.toUpperCase()} ${response.config.url}`, {
      status: response.status,
      data: response.data
    });
    return response;
  },
  (error) => {
    console.error('‚ùå Erreur de r√©ponse:', error);

    // Rediriger vers la page de login si le token est invalide/expir√©
    if (error.response && error.response.status === 401) {
      console.log('‚ö†Ô∏è Session expir√©e, redirection vers la page de connexion');
      localStorage.removeItem('token');
      window.location.href = '/login';
    }

    return Promise.reject(error);
  }
);

export default api;
