import React from 'react';
import { Link } from 'react-router-dom';
import { siteContent } from '../data/data';
import { useAuth } from '../contexts/AuthContext';

const Navbar = () => {
  const { navigation } = siteContent;
  const { isAuthenticated, user } = useAuth();

  return (
    <nav className="bg-white shadow-sm">
      <div className="container mx-auto px-4 py-4">
        <div className="flex justify-between items-center">
          <div className="flex items-center">
            <Link to="/" className="text-lg font-bold">
              {navigation.logo}
            </Link>
            <div className="ml-8 relative hidden md:block">
              <div className="absolute inset-y-0 left-0 flex items-center pl-3 pointer-events-none">
                <svg className="w-4 h-4 text-gray-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
              </div>
              <input
                type="text"
                placeholder={navigation.search}
                className="pl-10 pr-4 py-2 w-64 bg-gray-100 rounded-lg focus:outline-none focus:bg-white focus:ring-1 focus:ring-gray-200"
              />
            </div>
          </div>
          <div className="hidden md:flex items-center space-x-8">
            <Link to="/" className="text-gray-700 hover:text-gray-900">
              {navigation.discover}
            </Link>
            <Link to="/restaurants" className="text-gray-700 hover:text-gray-900">
              {navigation.restaurants}
            </Link>
            <Link to="/menu" className="text-gray-700 hover:text-gray-900">
              {navigation.menu}
            </Link>
            {isAuthenticated ? (
              <Link to="/profile" className="flex items-center">
                <img
                  src={user?.avatar || "https://randomuser.me/api/portraits/men/32.jpg"}
                  alt="Profile"
                  className="w-8 h-8 rounded-full"
                />
              </Link>
            ) : (
              <Link to="/login" className="bg-black text-white px-4 py-2 rounded-md">
                {navigation.signIn}
              </Link>
            )}
          </div>
          <div className="md:hidden">
            <button className="text-gray-500 hover:text-gray-700 focus:outline-none">
              <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
              </svg>
            </button>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
